```typescript
import * as pulumi from "@pulumi/pulumi";
import * as auth0 from "@pulumi/auth0";

const myHook = new auth0.Hook("myHook", {
    dependencies: {
        auth0: "2.30.0",
    },
    enabled: true,
    script: `    function (user, context, callback) {
      callback(null, { user });
    }

`,
    secrets: {
        foo: "bar",
    },
    triggerId: "pre-user-registration",
});
